import React, { Component } from 'react';
import axios from 'axios';

import './Widget.css';

class Mot extends Component {
	constructor(props) {
		super(props)

		this.state = {
			track: null
		}
	}

	componentDidMount() {
		this.sync()
	}

    render() {
        
		const { track } = this.state;
		
		console.log(track)
		if (track != null) {
			return <p>La chanson du jour est : { this.state.track }</p>
		}

    }

	sync() {
		axios.get("http://localhost:3000/Track")
			.then(({data}) => this.setState({ track: data.lenght }))
	}
}
export default Mot;
